CREATE TABLE JOGOS (
    id varchar(20) PRIMARY KEY,
    nome VARCHAR(100),
    franquia VARCHAR(100),
    genero1 VARCHAR(50),
    genero2 VARCHAR(50),
    plataforma VARCHAR(50),
    publicadora VARCHAR(100),
    nr_jogadores INT,
    nota_metascore DECIMAL(3,1),
    nota_ign DECIMAL(3,1),
    data_lancamento DATE
);


